import React ,{Component}from 'react';
import { connect } from 'react-redux'
import {loadUsers} from '../action/index'

class Hello extends Component {
  componentDidMount() {
    this.props.loadUsers();
};
  render(){
    if (this.props.loading) {
      return <div>Loading</div>
  }
    if (this.props.error){
    return<div style={{color:'red'}}><center>Error:{this.props.error}</center></div>
    }
  return (
      <div>
      <center>
      <ul>   
            {this.props.data.map(el => (
          <li>Name:{el.myprofile.Name}<br></br>Age:{el.myprofile.Age}<br></br>Email:{el.myprofile.Email}</li>
        ))}
      </ul>
      </center>
      </div>
  );
}
}

const mapStateToProps = state => ({
  data: state.reduxSaga.data,
  loading: state.reduxSaga.loading,
  error: state.reduxSaga.error,
});
const mapDispatchToProps = {
  loadUsers
};

export default connect (mapStateToProps,mapDispatchToProps)(Hello);
