import { all, call, put, takeEvery } from 'redux-saga/effects';
import { LOAD_USERS_SUCCESS, LOAD_USERS_LOADING,LOAD_USERS_ERROR} from './index';

// export function* fetch() {
// const endpoint = '/details';
//   const response = yield call(fetch, endpoint);
//   const data = yield response.json();
//   yield put({ type:LOAD_USERS_LOADING, user: data });
// }

export function* loadusersuccess() {
  yield takeEvery(LOAD_USERS_SUCCESS, fetch);
}

export default function* rootSaga() {
  yield all([loadusersuccess()]);
}


function* fetch() {
  try {
    const data = yield call(() => {
      return fetch('/details')
              .then(res => res.json())
      }
    );
    yield put({ type:LOAD_USERS_LOADING, user: data });
  } catch (error) {
    yield put(requestDogError());
  }
}
