const initialState = {
  data: [],
  error: ''
};
export default function reducer(state = initialState, action) {
  switch (action.type) {
      // case 'LOAD_USERS_LOADING': {
      //     return {
      //         ...state,
      //         loading: true,
      //         error:''
      //     };
      // }
      case 'LOAD_USERS_SUCCESS': {
          return {
              ...state,
              data: action.data,
              
          }
      }
      case 'LOAD_USERS_ERROR': {
          return {
              ...state,
             error: action.error
          };
      }
      default: {
          return state;
      }
  }
}
