import React,{Component} from 'react'
import {Link} from 'react-router-dom';
import { connect } from 'react-redux'
import { put, takeEvery, takeLatest } from 'redux-saga/effects';
 

const LOAD_USERS_LOADING = 'REDUX_SAGA_LOAD_USERS_LOADING';
const LOAD_USERS_SUCCESS = 'REDUX_SAGA_LOAD_USERS_SUCCESS';
const LOAD_USERS_ERROR = 'REDUX_SAGA_LOAD_USERS_ERROR';
const loadusers = () => dispatch => {
  dispatch({ type: LOAD_USERS_LOADING });
};
// const users = () => 
//   dispatch => {
//     dispatch({type:LOAD_USERS_LOADING});
//       return fetch('/details')
//         .then(response => response.json())
//         .then(
//                userinfo =>dispatch({ type: LOAD_USERS_SUCCESS, userinfo }),
//                 error => dispatch({ type: LOAD_USERS_ERROR ,error }))
//     };
       
async function fetchAsync(func) {
  const response = await func();
  if (response.ok) {
      return await response.json();
  }
  throw new Error("Unexpected error!!!");
}
function* fetchusers() {
  try {
      const users = yield fetchAsync('/details');
      yield put({type: LOAD_USERS_SUCCESS, userinfo : users});
  } catch (e) {
      yield put({type: LOAD_USERS_ERROR, error: e.message});
  }
}
export function* rootSaga() {
  
  yield takeEvery(LOAD_USERS_LOADING, fetchusers);
 }

