import React,{Component} from 'react'
import {Link} from 'react-router-dom';
import { connect } from 'react-redux'
import axios from 'axios'

 const LOAD_USERS_LOADING = 'REDUX_THUNK_LOAD_USERS_LOADING';
 const LOAD_USERS_SUCCESS = 'REDUX_THUNK_LOAD_USERS_SUCCESS';
 const LOAD_USERS_ERROR = 'REDUX_THUNK_LOAD_USERS_ERROR';
//  const users = () => {
//     return  (dispatch) => {
//           return fetch('/details')
//               .then(response => response.json())
//               .then(json => dispatch(
//               { type: "DATA_LOADED", data: json }))
//               .catch(err => dispatch(
//                 { type: "ERROR",msg: "Unable to fetch data" }))
//               }
//         }

        const users = () => {
          return(dispatch) => {     //nameless functions
            // Initial action dispatched
            dispatch({ type: "LOAD_USERS_LOADING" });
            // Return promise with success and failure actions
            return axios.get('/details').then(  
            userinfo=>dispatch({type:"LOAD_USERS_SUCCESS",userinfo}) ,
             err => dispatch({ type: "LOAD_USERS_ERROR",err })
            );
          };
        };

 class Menu extends Component {
    render() {
        return (
            <div >
              <div>
              <center><label>Menu</label></center>
              <ul>
              <li> <Link to ="/">Userinfo</Link> </li>
              <li><Link to ="/Hi">Friends</Link></li>
              </ul>
            </div>
            </div>

        )
        }
    }

  
    const mapDispatchtoProps = (dispatch) => {
        return {
           users: () => dispatch(users()) 
        }
      }
  
 export default connect(mapDispatchtoProps,users) (Menu)
