import { all, call, put, takeEvery } from 'redux-saga/effects';
import {FETCHED,LOAD_USERS_SUCCESS, LOAD_USERS_LOADING,LOAD_USERS_ERROR} from './index';

// export function* fetch() {
// const endpoint = '/details';
//   const response = yield call(fetch, endpoint);
//   const data = yield response.json();
//   yield put({ type:LOAD_USERS_LOADING, user: data });
// }

function* fetch() {
    try {
      yield put({type:LOAD_USERS_LOADING});
      const data = yield call(() => {
        return fetch('/details')
                .then(res => res.json())
            }
      );
      yield put({ type: LOAD_USERS_SUCCESS, data });
    } catch (error) {
      yield put({ type: LOAD_USERS_ERROR ,error });
    }
  }

export function* fetched() {
  yield takeEvery(FETCHED, fetch);
}

export default function* rootSaga() {
  yield all([fetched()]);
}
