import React, { Component } from 'react';
import { connect } from 'react-redux'
import { DATA_LOADED } from '../src/Containers/Users'


class App extends Component {

  componentDidMount() {
    this.props.onDATA_LOADED()
  }

  render() {
    return (
      <div className="App">
       <h1>Fetching the data from the backend</h1>
       

        {this.props.articles && <ul>
          <li>id: {this.props.remoteArticles.id}</li>
          <li>title: {this.props.remoteArticles.title}</li>
        </ul>}
      </div>
    );
  }
}


const mapStatetoProps = (state) => {
  return { articles: state.remoteArticles }
}

const mapDispatchtoProps = (dispatch) => {
  return {
     onDATA_LOADED: () => dispatch(DATA_LOADED()) 
  }
}

export default connect(mapStatetoProps, mapDispatchtoProps)(App);
