    render() {
        return (
            <div >
              <div>
              <center><label>Menu</label></center>
              <ul>
              <li> <Link to ="/">Userinfo</Link> </li>
              <li><Link to ="/Hi">Friends</Link></li>
              </ul>
            </div>
            </div>

        )
    }
}
  const mapDispatchToProps = (dispatch)=>{
  return{
  getData:() => dispatch(getData())
  }
};

export default connect (mapDispatchToProps,{getData})(Menu)
