import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import {BrowserRouter} from 'react-router-dom';
import { createStore,applyMiddleware,compose } from 'redux'
import { Provider } from 'react-redux'
import reducer from './reducers/index'
import createSagaMiddleware from 'redux-saga';
//import usersSaga from './action/index'
import Redux from "redux-thunk" 
import handleNewMessage from './action/index'

const sagaMiddleware = createSagaMiddleware();
const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
const store = createStore(reducer,composeEnhancer(applyMiddleware(sagaMiddleware, Redux)));
sagaMiddleware.run(handleNewMessage);

ReactDOM.render(
<BrowserRouter>
<Provider store={store}>
    <App />
</Provider>
</BrowserRouter>, document.getElementById('root'));
