import React  from 'react';
import { connect } from 'react-redux'
import {getData} from '../action/index'

class Hi extends React.Component {
  componentDidMount() {
    this.props.getData();
  }
  
  
  render(){
    
  
    return (
      <div>
      <center>
      <ul>  
            {this.props.articles.map(el => (
          <li>My friends are:<br></br>{el.buddy.firstfriendname}<br></br>{el.buddy.secondfriendname}</li>
        ))}
      </ul>
      </center>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    articles: state.remoteArticles.slice(0,1)
  };
}
export default connect (mapStateToProps,{getData})(Hi);
