import React ,{Component}from 'react';
import { connect } from 'react-redux'



 export class Hello extends Component {

  render(){
    if (this.props.loading) {
      return <div>Loading</div>
   }
  if (this.props.error){
 return<div style={{color:'red'}}><center>Error:{this.props.error}</center></div>
    }
  return (
      <div>
      <center>
      <ul>   
            {this.props.articles.map(u => (
          <li>Name:{u.myprofile.Name}<br></br>Age:{u.myprofile.Age}<br></br>Email:{u.myprofile.Email}</li>
        ))}
      </ul>
      </center>
      </div>
  );
}
}

function mapStateToProps  (state)  {
  
 return{ 
  articles:state.remoteArticles
  };
  
}

export default connect (mapStateToProps)(Hello);
