const initialState = {
    articles: []
    
  };
  
  function reducer(state = initialState, action) {
  
    if (action.type === "DATA_LOADED") {
      return Object.assign({}, state, {
        articles: state.articles.concat(action.payload)
      });
    }
    return state;
  }
  
  export default reducer;
