import React, { Component } from 'react';
import { connect } from 'react-redux'
import {Link,Route} from 'react-router-dom';
//import {getData} from './Containers/Users'

export function getData () {
  return function (dispatch) {
        return fetch('/userinfo')
            .then(response => response.json())
            .then(json => dispatch(
                { type: "DATA_LOADED", data: json }))
           
    }
  }


  class App extends Component {
  constructor(props) {
    super(props);
  }
  componentDidMount() {
    this.props.getData();
  }

render() {
  return (
    <div className='grid-container'>
      <div >
        <center><label>Menu</label></center>
        <ul>
        <li> <Link to ="/">Userinfo</Link> </li>
        </ul>
      </div>

      <div className='item2'>
        <center><label>Contents</label></center>
          <center>
          <Route path="/" exact component={getData}/>  
 
            <ul>
            {this.props.userinfo.map(el => (
          <li>Name:{el.Name}<br></br>Age:{el.Age}<br></br>Email:{el.Email}</li>
        ))}
      </ul> *
      </center>
      </div>
      </div>
       
    );
  }
}


const mapStatetoProps=(state)=> {
  return {userinfo: state.userinfo };
}

const mapDispatchtoProps = (dispatch) => {
  return {
     getData: () => dispatch(getData()) 
  }
}

export default connect(mapStatetoProps,mapDispatchtoProps)(App);
