import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import {BrowserRouter} from 'react-router-dom';
import { createStore,applyMiddleware,compose } from 'redux'
//import reducer from './reducers/index'
import createSagaMiddleware from 'redux-saga';
//import Fetch from "./action/index";
import {Provider} from 'react-redux'
import { connect } from 'react-redux'
//import { loadToDoList } from './action/index';
import toDoApp from './reducers/index';
import userinfo from './action/store';
const sagaMiddleware = createSagaMiddleware();
const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
const store = createStore(toDoApp,composeEnhancer(applyMiddleware(sagaMiddleware)));
sagaMiddleware.run( userinfo );
    
    
//store.dispatch(loadToDoList());
const ConnectedApp = connect((state) => {
    console.log(state);
    return state;
  })(App);

ReactDOM.render(
<Provider store ={store}> 
<BrowserRouter>
<ConnectedApp />
</BrowserRouter>
</Provider>  , document.getElementById('root'));
