const reducer = (state = initialState, action) => {

  switch (action.type) {
      case "DATA_LOADED":
          return { ...state, userinfo: action.data }
      case "ERROR":
          return { ...state, error: action.msg }
      default:
          return state
  }

}

export default reducer
