import React  from 'react';
import { connect } from 'react-redux'


class Hi extends React.Component {
  render(){
    
    if (this.props.error){
      return<div style={{color:'red'}}><center>Error:Failed to Fetch</center></div>
    }
    return (
      <div>
      <center>
      <ul>  
            {this.props.user.map(el => (
          <li>My friends are:<br></br>{el.buddy.firstfriendname}<br></br>{el.buddy.secondfriendname}</li>
        ))}
      </ul>
      </center>
      </div>
    );
  }
}

const mapStatetoProps=(state)=> ({
  user: state.user,
  loading: state.loading,
 
});

export default connect (mapStatetoProps)(Hi);
