// };

// const hi = (data) => {
//   return { type: 'HI', url: data.message }
// };

// const bye = () => {
//   return { type: 'BYE' }
// };

 const users = () => {
    return  (dispatch) => {
          return fetch('/details1')
              .then(response => response.json())
              .then(json => dispatch(
              { type: "DATA_LOADED", data: json }))
              .catch(err => dispatch(
                { type: "ERROR",msg: "Unable to fetch data" }))
              }
        }

        // const users = () => {
        //   return (dispatch) => {
        //     dispatch(hello());
        //     fetch('/details')
        //       .then(res => res.json())
        //       .then(
        //         data => dispatch(hi(data)),
        //         err => dispatch(bye())
        //       );
        //   }
        // };

 class Menu extends Component {
    render() {
        return (
            <div >
              <div>
              <center><label>Menu</label></center>
              <ul>
              <li> <Link to ="/">Userinfo</Link> </li>
              <li><Link to ="/Hi">Friends</Link></li>
              </ul>
            </div>
            </div>

        )
        }
    }

  
    const mapDispatchtoProps = (dispatch) => {
        return {
           users: () => dispatch(users()) 
        }
      }
  
 export default connect(mapDispatchtoProps,users) (Menu)
