const initialState = {
userinfo:null,
 error:""
}
function reducer(state = initialState, action) {
 if (action.type === "DATA_LOADED") {
    return Object.assign({}, state, {
      userinfo: state.userinfo.concat(action.data)
    });
  }
  if (action.type === "ERROR") {
    return Object.assign({}, state, {
      error: state.error.concat(action.msg)
    });
  }
  return state;
}
