import React, { Component } from 'react';
import { connect } from 'react-redux'
import {users} from '../Containers/menu'


  class Hello extends Component {
    constructor(props) {
      super(props);
    }
    componentDidMount() {
      this.props.users();
    }
  
  render() {
    return (
      <div>
      <center>
      <ul>
            {this.props.userinfo.map(el => (
          <li>Name:{el.myprofile.Name}<br></br>Age:{el.myprofile.Age}<br></br>Email:{el.myprofile.Email}</li>
        ))}
      </ul>
      </center>
      </div>
    );
  }
}

const mapStatetoProps=(state)=> {
  return {userinfo: state.userinfo.slice(0,1) };
}

const mapDispatchtoProps = (dispatch) => {
  return {
     users: () => dispatch(users()) 
  }
}

export default connect(mapStatetoProps,mapDispatchtoProps)(Hello);
