import React,{Component} from 'react'
import {Link} from 'react-router-dom';
import { connect } from 'react-redux'

const hello = () => {
  return { type: 'HELLO' }
};

const hi = (data) => {
  return { type: 'HI', url: data.message }
};

const bye = () => {
  return { type: 'BYE' }
};

//  const users = () => {
