import React, { Component } from 'react';
import { connect } from 'react-redux'

function comrade () {
    return function (dispatch) {
          return fetch('/details')
              .then(response => response.json())
              .then(json => dispatch(
              { type: "DATA_LOADED1", data: json }))
             
      }
    }

    
  class Hi extends Component {
    constructor(props) {
      super(props);
    }
    componentDidMount() {
      this.props.comrade();
    }
  
  render() {
    return (
      <div>
      <center>
      <ul>
            {this.props.friends.map(el => (
          <li>My friends are:<br></br>Name:{el.buddy.firstfriendname}<br></br>Name:{el.buddy.secondfriendname}</li>
        ))}
      </ul>
      </center>
      </div>
    );
  }
}

const mapStatetoProps=(state)=> {
  return {friends: state.friends};
}

const mapDispatchtoProps = (dispatch) => {
  return {
     comrade: () => dispatch(comrade()) 
  }
}

export default connect(mapStatetoProps,mapDispatchtoProps)(Hi);
