const reducer = (state = initialState, action) => {

  switch (action.type) {
    case'HELLO':
    return{
      userinfo:action.userinfo,
      loading:false,
      error:false,
    };
    case'HI':
    return {
      userinfo :'',
      loading: false,
      error: true,
    };
    case'BYE':
    return{
      userinfo:'',
      loading: false,
      error: true,
    };
    
    }
    return state;
  }
export default reducer
