import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { fetchRecipes,recipesSelector} from '../reducers/index'

const Hi = () => {
  //const dispatch = useDispatch()
  const { recipes, loading, hasErrors } = useSelector(recipesSelector)

  // useEffect(() => {
  //   dispatch(fetchRecipes())
  // }, [dispatch])

  const renderRecipes = () => {
    if (loading) return <p>Loading recipes...</p>
    if (hasErrors) return <p>Cannot display recipes...</p>

    return recipes.map(recipe =>
      <div >
     <center><h2>Name:{recipe.buddy.firstfriendname}<br></br>Age:{recipe.buddy.secondfriendname}</h2></center> 
      </div>
    )
  }

  return (
    <section>
      <div>
        {renderRecipes()}
      </div>
    </section>
  )
}


export default Hi;
