import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux'
import {fetchUser, userSelector} from '../reducers/index'

const Hello = () => {
 
  
const { user, loading, hasErrors } = useSelector(userSelector)
const dispatch = useDispatch()

useEffect(() => {
  dispatch(fetchUser())
    }, [dispatch])
  

  const renderUser = () => {
    if (loading) return <center> <p>Loading user...</p></center>
    if (hasErrors) return <center> <p>Cannot display user...</p></center> 

    return user.map(user =>
      <div >
     <center><font Size='+2.5'><h2>Name:{user.myprofile.Name}<br></br>Age:{user.myprofile.Age}<br></br>Email:{user.myprofile.Email}</h2></font></center> 
      </div>
    )
  }

  return (
    <section>
      <div>
        {renderUser()}
      </div>
    </section>
  )
}


export default Hello;
