import React,{Component} from 'react'
import {Link} from 'react-router-dom';
import { connect } from 'react-redux'


//  export const users =()=> {
//      return (dispatch)=> {
//           return fetch('/details')
//               .then(response => response.json())
//               .then(json => dispatch(
//               { type: "DATA_LOADED",userinfo:json }))
//         }
//  }
function fetchRecipes() {
    return async dispatch => {
      dispatch(getRecipes())
  
      try {
        const response = await fetch('/details')
        const data = await response.json()
  
        dispatch(getRecipesSuccess(data))
      } catch (error) {
        dispatch(getRecipesFailure())
      }
    }
  }


 class Menu extends Component {
    render() {
        return (
            <div >
              <div>
              <center><label>Menu</label></center>
              <ul>
              <li> <Link to ="/">Userinfo</Link> </li>
              <li><Link to ="/Hi">Friends</Link></li>
              </ul>
            </div>
            </div>

        )
        }
    }

  
    const mapDispatchtoProps = (dispatch) => {
        return {
            fetchRecipes: () => dispatch(fetchRecipes()) 
        }
      }
  
 export default connect(mapDispatchtoProps,fetchRecipes) (Menu)
