import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux'
import {fetchUser, userSelector} from '../reducers/index'

const Hi = () => {
  const dispatch = useDispatch()
  const { user, loading, hasErrors } = useSelector(userSelector)

   useEffect(() => {
   dispatch(fetchUser())
   }, [dispatch])

  const renderUser = () => {
    if (loading) return <center><p>Loading user...</p></center>
    if (hasErrors) return <center><p>Cannot display user...</p></center>

    return user.map(user =>
      <div >
     <center><font Size='+2'><h2>My Friends are:<br></br>Name:{user.buddy.firstfriendname}<br></br>Name:{user.buddy.secondfriendname}</h2></font></center> 
      </div>
    )
  }

  return (
    <section>
      <div>
        {renderUser()}
      </div>
    </section>
  )
}


export default Hi;
